CREATE TABLE BOARD (IDBOARD  SERIAL NOT NULL, BOARDNAME VARCHAR(255), BOARDSTATUS VARCHAR(255), BOARDSTORIE VARCHAR(255), ENDDATE_BOARD TIMESTAMP, STARTDATE_BOARD TIMESTAMP, PRIMARY KEY (IDBOARD))
CREATE TABLE MEMBER (IDMEMBER  SERIAL NOT NULL, ACTIVITY_DOMANIN VARCHAR(255), MEMBER_NAME VARCHAR(255), PASSWORD VARCHAR(255), USERROLE VARCHAR(255), USERNAME VARCHAR(255), PRIMARY KEY (IDMEMBER))
CREATE TABLE SUBTASK (IDSUBTASK  SERIAL NOT NULL, SUBTASK_DESCRIPTION VARCHAR(255), SUBTASK_NAME VARCHAR(255), SUBTASK_STATUS VARCHAR(255), SUBTASK_MEMBER_IDMEMBER INTEGER, SUBTASK_TASK_IDTASK INTEGER, PRIMARY KEY (IDSUBTASK))
CREATE TABLE TASK (IDTASK  SERIAL NOT NULL, TASKDESCRIPTION VARCHAR(255), TASKNAME VARCHAR(255), TASKSTATUS VARCHAR(255), TASK_TASKLIST_IDTASKLIST INTEGER, PRIMARY KEY (IDTASK))
CREATE TABLE TASKLIST (IDTASKLIST  SERIAL NOT NULL, ENDDATE_TASKLIST TIMESTAMP, STARTDATE_TASKLIST TIMESTAMP, TASKLISTNAME VARCHAR(255), TASKLISTSTATUS VARCHAR(255), BOARD_TASKLIST_IDBOARD INTEGER, TEAM_TASKLIST_IDTEAM INTEGER, PRIMARY KEY (IDTASKLIST))
CREATE TABLE TEAM (IDTEAM  SERIAL NOT NULL, TEAMNAME VARCHAR(255), PRIMARY KEY (IDTEAM))
CREATE TABLE TASK_MEMBER (task_memberList_idMember INTEGER NOT NULL, member_taskList_idTask INTEGER NOT NULL, PRIMARY KEY (task_memberList_idMember, member_taskList_idTask))
CREATE TABLE TEAM_MEMBER (team_memberList_idMember INTEGER NOT NULL, member_teamList_idteam INTEGER NOT NULL, PRIMARY KEY (team_memberList_idMember, member_teamList_idteam))
ALTER TABLE SUBTASK ADD CONSTRAINT FK_SUBTASK_SUBTASK_TASK_IDTASK FOREIGN KEY (SUBTASK_TASK_IDTASK) REFERENCES TASK (IDTASK)
ALTER TABLE SUBTASK ADD CONSTRAINT FK_SUBTASK_SUBTASK_MEMBER_IDMEMBER FOREIGN KEY (SUBTASK_MEMBER_IDMEMBER) REFERENCES MEMBER (IDMEMBER)
ALTER TABLE TASK ADD CONSTRAINT FK_TASK_TASK_TASKLIST_IDTASKLIST FOREIGN KEY (TASK_TASKLIST_IDTASKLIST) REFERENCES TASKLIST (IDTASKLIST)
ALTER TABLE TASKLIST ADD CONSTRAINT FK_TASKLIST_TEAM_TASKLIST_IDTEAM FOREIGN KEY (TEAM_TASKLIST_IDTEAM) REFERENCES TEAM (IDTEAM)
ALTER TABLE TASKLIST ADD CONSTRAINT FK_TASKLIST_BOARD_TASKLIST_IDBOARD FOREIGN KEY (BOARD_TASKLIST_IDBOARD) REFERENCES BOARD (IDBOARD)
ALTER TABLE TASK_MEMBER ADD CONSTRAINT FK_TASK_MEMBER_member_taskList_idTask FOREIGN KEY (member_taskList_idTask) REFERENCES TASK (IDTASK)
ALTER TABLE TASK_MEMBER ADD CONSTRAINT FK_TASK_MEMBER_task_memberList_idMember FOREIGN KEY (task_memberList_idMember) REFERENCES MEMBER (IDMEMBER)
ALTER TABLE TEAM_MEMBER ADD CONSTRAINT FK_TEAM_MEMBER_team_memberList_idMember FOREIGN KEY (team_memberList_idMember) REFERENCES MEMBER (IDMEMBER)
ALTER TABLE TEAM_MEMBER ADD CONSTRAINT FK_TEAM_MEMBER_member_teamList_idteam FOREIGN KEY (member_teamList_idteam) REFERENCES TEAM (IDTEAM)
